import pytest
import main


class SQSMock:
    def send_message(self, QueueUrl, MessageBody):
        assert MessageBody == '{"user_id": "123", "amount": 20000.0}', "validate transaction message"
        assert QueueUrl == main.TRANSACTION_QUEUE

        return {
            "ResponseMetadata": {
                "HTTPStatusCode": 200
            }
        }


def test_handler():
    main.sqsClient = SQSMock()

    response = main.lambda_handler(
        {
            "Records": [
                {
                    "body": "",
                }
            ]
        }, {})
    assert None == response, "Validate output"
